package serial

import (
	"encoding/hex"
	"log"
	"testing"
)

func TestSender(t *testing.T) {
	cases := []struct {
		in   string
		want string
	}{
		//		{"024646323131303020202020203020202952402020202020202020202020202020202020202020202020202020203337463003", ""},
		//		{"commands\r", ""},
		//		{"024646303131303020202020203020203342202020202020202020202020202020202020202020202020202020203437303603", ""},
		//	{"06300230303030303242334203", ""},
		{"02303132303030303735433003", ""},

		//		{"02", ""},
	}

	for _, c := range cases {
		Open("com6", 115200)
		defer Close()
		hex.DecodeString(c.in)
		input, _ := hex.DecodeString(c.in)
		output := Sender(input)
		log.Println("input:", input)
		log.Println("output", output)
		got := hex.EncodeToString(output)

		if got != c.want {
			t.Errorf("Sender()\nwant\t%s\ngot\t%s\n", c.want, got)
		}
	}
}

